import { Link } from "react-router-dom";
import MenuAdmin from "../../../components/pagAdm";
import "./index.scss"
import "../../../common/common.scss"
import { listarProduto } from "../../../../../api/src/repository/produtoRepository";
import { useState } from "react";

export default function Index(){
    const[produtos, setProdutos] = useState([])

    async function carregarTodosProdutos(){
        const resp= await listarProduto();
        setProdutos(resp)
    }
    return(
        <main className="Novo-Produto">

             <MenuAdmin logo='../../../images/logoAdmin.png'/>

            <div className="fundo">
                <div>
                    <Link className="edit" to='/AddProduto'>Novo Produto</Link>
                </div>

                <div>
                    <input placeholder="digite o tema"></input>
                    <img src="../../../images/lupa.png"/>
                    <input placeholder="digite a categoria"></input>
                    <img src="../../../images/lupa.png"/>

                </div>

                    <div className="cardProdutos">
                        <p>funko tal</p>
                        <div>
                        <img src='../../../images/lixeira.png'/>
                        <img src='../../../images/editar.png'/>

                        </div>
                </div>
            </div>

           
            
        </main>
    )
}
