import './index.scss'

import Cabecalho from "../../../components/cabecalho.js";
import CompProdutos from '../../../components/bannerPag';
import { useEffect, useState } from 'react';
import { buscarProdutosPorNome, ProdutosListados } from '../../../API/CadProduto';


export default function Index(){
    const [produtos, setProdutos] = useState([]);

    const [busca, setBusca] = useState('');

    async function ListarProdutos(){
        const resposta= await ProdutosListados();
        setProdutos(resposta)
    }

    
        useEffect(() => {
            ListarProdutos();
        } , [])

    async function buscarNomeClick() {
        const resp = await buscarProdutosPorNome(busca);
        console.log(resp)
        setProdutos(resp);
     }




    return(
        <main>
            <Cabecalho logo='../../../images/logoAdmin.png' menu='../../../images/menu.png'  fav='../../../images/favoritos.png' 
            user='../../../images/user.png' sacola='../../../images/sacola.png' placeholder='encontre seu produto aqui' pes type='text' value={busca} onChange={e=> setBusca(e.target.value)} />

            <CompProdutos banner='../../../images/bannerNaruto.png'/>
        
            <div>
                    {produtos.map(item =>
                    <div>
                        <p>{item.nome}</p>
                        <p>{item.preco}</p>
                    </div>
                        
                     )}
                </div>
        
        </main>
    )
}
