import { useState } from "react";
import { Link } from "react-router-dom";
import { loginUsuario } from "../../../API/logAdm";
import './index.scss';

export default function Index() {

    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    async function login(){
        console.log(email)
        console.log(senha)
        const a = await loginUsuario(email, senha);
        alert('logado')
    }

    return(
        <main className="login">

            <div className="fundo">
                <h1> Entre Agora! </h1>

                <div>
                    <div className="espaçamento">
                        <img src='../images/user (1).png' alt=""/>
                        <input placeholder="E-Mail" type='text' value={email} onChange={e => setEmail(e.target.value)} />
                    </div>

                    <div>
                        <img src='../images/padlock.png' alt=''/>
                        <input placeholder="Senha" type='text' value={senha} onChange={e => setSenha(e.target.value)} />
                    </div>
                </div>

                
                <div>
                    <button onClick={login}> Entrar </button>

                    <p> Ainda não possui cadastro? <Link to='/usuario/cadastro'> Cadastre-se agora! </Link></p>

                    <Link to='/'> Voltar</Link>

                    <Link to='/loginAdmin'> <img src='' alt='' /> </Link>
                      
                </div>
            </div>

        </main>
    )
}
