import Cabecalho from "../../../components/cabecalho.js";

export default function Index(){
    const[produto, setProduto] = useState([]);


    async function ListarProdutos(){
        const resposta= await ProdutosListado();
        setProduto(resposta)
    }

    
        async function Lista(){
            const resposta= await Listar();
            setHeroi(resposta)
        }
    
        useEffect(() => {
            Lista();
        } , [])
    
    return(
        <main>
            <Cabecalho logo='../../../images/logoAdmin.png' menu='../../../images/menu.png'  fav='../../../images/favoritos.png' user='../../../images/user.png' sacola='../../../images/sacola.png'/>
        
            <div>
                    {produto.map(item =>
                    <div>
                        <p>{item.nome}</p>
                        <button onClick={() => DeletarProdutos(item.id)} ><img src="../../../images/lixeira.png"/></button>  
                        <button><img src="../../../images/editar.png"/></button>  
                        
                    </div>
                        
                     )}
                </div>
        
        </main>
    )
}
