import { Link } from "react-router-dom";
import MenuAdmin from "../../../components/pagAdm";
import "./index.scss"
import "../../../common/common.scss"
import { useEffect, useState } from "react";
import { listarCategorias, listarTemas } from "../../../API/CadProduto";

export default function Index(){
    const[produtos, setProdutos] = useState([])

    const [filtroTema, setFiltroTema] = useState('');
    const [filtroCategoria, setFiltroCategoria] = useState('');


    async function carregarTodosTemas(){
        const resp= await listarTemas(filtroTema);
        setProdutos(resp);
    }

    async function carregarTodasCategorias(){
        const res= await listarCategorias(filtroCategoria);
        setProdutos(res);
    }


    
    return(
        <main className="Novo-Produto">

             <MenuAdmin logo='../../../images/logoAdmin.png'/>

            <div className="fundo">
                <div>
                    <Link className="edit" to='/AddProduto'>Novo Produto</Link>
                </div>

                <div>
                    <input type='text'  placeholder="digite o tema" value={filtroTema} onChange={e => setFiltroTema()}/>
                    <img src="../../../images/lupa.png"/>
                    <input placeholder="digite a categoria"></input>
                    <img src="../../../images/lupa.png"/>

                </div>

                    <div className="cardProdutos">
                        <p>funko tal</p>
                        <div>
                        <img src='../../../images/lixeira.png'/>
                        <img src='../../../images/editar.png'/>

                        </div>
                </div>
            </div>

           
            
        </main>
    )
}
