import { API_URL } from '../../API/config';
import './carrinhoItem.scss'


export default function CarrinhoItem({item:{ produto:{info, tema, destaque}, qtd }}){
    function ExibirImagemPrincipal(){
        return API_URL + '/' + produto.destaque.url;
    }
    
    return(
        <div>
            <div>
                <img src={ExibirImagemPrincipal()}></img>
            </div>
            <div>
                <p>{info.NomeTema}</p>
            </div>

            <div>
                <p>{info.nome}</p>
            </div>
            <div>
                <p>R${info.preco}</p>
            </div>
            <div>
                <p>categoria</p>
            </div>

        </div>
    )
}
